<style lang="less">
.app-logo {
  .st2 {
    fill: #64748b;
  }
  .st1 {
    fill: var(--color-body);
    transition: all 0.1s ease;
  }
  .st0 {
    fill: var(--color-primary);
  }
  &.scheme-subtle {
    .st1 {
      fill: theme("colors.slate.300");
    }
    .st0 {
      fill: theme("colors.slate.300");
    }
  }
  &.scheme-light {
    .st1 {
      fill: #fff;
    }
    .st0 {
      fill: theme("colors.primary.500");
    }
  }

  &.scheme-text {
    .st1 {
      fill: currentColor;
    }
    .st0 {
      fill: currentColor;
    }
  }

  &:hover .kaption-arrow {
    animation: kaboom 2s ease-in-out alternate infinite;
    transform-origin: 50% right;
    animation-delay: 0.4s;
    &:nth-child(2) {
      animation-delay: 0.2s;
    }
    &:nth-child(3) {
      animation-delay: 0s;
    }
  }
  // https://css-tricks.com/animating-svg-css/
  @keyframes kaboom {
    0% {
      fill: theme("colors.red.400");
    }
    25% {
      fill: rgba(82, 51, 255, 0.9);
    }

    50% {
      fill: rgba(82, 51, 255, 1);
    }
    100% {
      fill: rgba(82, 51, 255, 1);
    }
  }
}
</style>
